declare module "*.svg" {
  const content: string;
  export default content;
}

// Declaration for PNG files
declare module "*.png" {
  const content: string;
  export default content;
}

// Declaration for JPEG/JPG files
declare module "*.jpeg" {
  const content: string;
  export default content;
}
declare module "*.jpg" {
  const content: string;
  export default content;
}

// Declaration for JSON files
declare module "*.json" {
  const value: any;
  export default value;
}

// Declaration for Fonts
declare module "*.ttf" {
  const content: string;
  export default content;
}
